sys_script_include.script:
var EventScriptIncludeUI = Class.create();
EventScriptIncludeUI.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    getUpcomingEvents: function() {
        var events = [];
        var gr = new GlideRecord('x_1419495_event_event_record');
        var today = new GlideDateTime();
        gr.addQuery('u_event_date', '>=', today);
        gr.orderBy('u_event_date');
        gr.query();

        while (gr.next()) {
            events.push({
                name: gr.getValue('u_event_name'),
                date: gr.getValue('u_event_date'),
                location: gr.getValue('u_location')
            });
        }

        return JSON.stringify(events);
    },
    type: 'EventScriptIncludeUI'
});
